package com.chainsys.day6;

public class Addition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		        int a = 4;
		        a++;
		        a += a;
		        --a;
		        a = 7 + a;
		        a *= a;
		        a -= 3;

		        System.out.println(" a = " + a );
	
	}

}
